import React, { useState, useEffect } from "react";
import RoomResult from "../common/RoomResult";
import RoomSearch from "../common/RoomSearch";

const HomePage = () => {
    const [roomSearchResults, setRoomSearchResults] = useState([]);
    const [currentSlide, setCurrentSlide] = useState(0);

    // Function to handle search results
    const handleSearchResult = (results) => {
        setRoomSearchResults(results);
    };

    const changeSlide = (direction) => {
        const slides = document.querySelectorAll('.slide');
        const totalSlides = slides.length;

        setCurrentSlide((prev) => (prev + direction + totalSlides) % totalSlides);
    };

    useEffect(() => {
        const interval = setInterval(() => {
            changeSlide(1);
        }, 5000); // Auto-slide every 5 seconds

        return () => clearInterval(interval); // Clean up the interval on component unmount
    }, []);

    return (
        <div className="home">
            {/* HEADER / BANNER ROOM SECTION */}
            <section>
                <header className="header-banner">
                    <div className="slider-container">
                        <div className="slider" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
                            <div className="slide">
                                <img src="./assets/images/taj5.jpg" alt="Taj Hotel" />
                            </div>
                            <div className="slide">
                                <img src="./assets/images/taj5.jpg" alt="Image 2" />
                            </div>
                            <div className="slide">
                                <img src="./assets/images/taj5.jpg" alt="Image 3" />
                            </div>
                            {/* Add more slides as needed */}
                        </div>
                        <div className="slider-controls">
                            <span className="prev" onClick={() => changeSlide(-1)}>&#10094;</span>
                            <span className="next" onClick={() => changeSlide(1)}>&#10095;</span>
                        </div>
                    </div>
                    <div className="overlay"></div>
                    <div className="animated-texts overlay-content">
                        <h1>
                            Welcome to <span className="taj-color">Taj Hotel</span>
                        </h1><br />
                        <h3>Step into a haven of comfort and care</h3>
                    </div>
                </header>
            </section>

            {/* SEARCH/FIND AVAILABLE ROOM SECTION */}
            <RoomSearch handleSearchResult={handleSearchResult} />
            <RoomResult roomSearchResults={roomSearchResults} />

            <h4><a className="view-rooms-home" href="/rooms">All Rooms</a></h4>

            {/* SERVICES SECTION */}
            <h2 className="home-services">Services at <span className="phegon-color">Taj Hotel</span></h2>
            <section className="service-section">
                <div className="service-card">
